userDefinedLinkHintCss:
:root {
  --vimium-background-color: #2e34407f;
  --vimium-background-text-color: #e3e3e3;
  --vimium-foreground-color: #4c566a7f;
  --vimium-foreground-text-color: #e8eaed;
  --vimium-link-color: #8ab4f8;
}
div > .vimiumHintMarker {
  background: #2e3440;
  border: 1px solid #88c0d0;
  padding: 5px;
  border-radius: 0.5rem;
}

div > .vimiumHintMarker span {
  color: #88c0d0;
  font-weight: bold;
  font-size: 12px;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #ECEFF4
}

pre, code {
  font-family: "CaskaydiaCove NFP" !important
}
#vomnibar li.selected {
  background-color: var(--vimium-foreground-color);
}
div.internal-vimium-hint-marker span {
  text-shadow: none
}
#vomnibar, #search-area, .vimium-body footer {
  box-shadow: none;
  backdrop-filter: blur(10px);
}
div#wrapper, #footer-content {
  max-width: calc(100vw - 50px) !important
}
.command {
  max-width: unset !important
}
